tape( 'the function throws an error if provided an `options` argument which is not an object', function test( t ) {
	var values;
	var i;

	function next( value, clbk ) {
		clbk( null, value );
	}

	function foo( resolve ) {
		resolve();
	}

	values = [
		'5',
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( [foo], value, next );
		};
	}
});

tape( 'the function throws an error if provided an invalid option', function test( t ) {
	var values;
	var i;

	function next( value, clbk ) {
		clbk( null, value );
	}

	function foo( resolve ) {
		resolve();
	}

	values = [
		'5',
		-5,
		3.14,
		0,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			var opts = {
				'limit': value
			};
			factory( [foo], opts, next );
		};
	}
});

tape( 'the function returns a function (no options)', function test( t ) {
	var f = factory( [noop], noop );
	t.equal( typeof f, 'function', 'returns a function' );
	t.end();
});

tape( 'the function returns a function (options)', function test( t ) {
	var f = factory( [noop], {}, noop );
	t.equal( typeof f, 'function', 'returns a function' );
	t.end();
});

tape( 'the returned function invokes the provided functions once and returns the results in order (asynchronous)', function test( t ) {
	var parallel;
	var expected;

	function one( resolve ) {
		setTimeout( onTimeout, 100 );
		function onTimeout() {
			resolve( null, 1 );
		}
	}

	function two( resolve ) {
		setTimeout( onTimeout, 400 );
		function onTimeout() {
			resolve( null, 2 );
		}
	}

	function three( resolve ) {
		setTimeout( onTimeout, 200 );
		function onTimeout() {
			resolve( null, 3 );
		}
	}

	parallel = factory( [one, two, three], done );

	expected = [
		1,
		2,
		3
	];

	parallel();

	function done( error, out ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.pass( 'did not return an error' );
		}
		t.deepEqual( out, expected, 'returns expected output' );
		t.end();
	}
});

