tape( 'the function supports specifying an execution context for the invoked function', function test( t ) {
	var parallel;
	var opts;
	var ctx;

	function one( resolve ) {
		/* eslint-disable no-invalid-this */
		this.count += 1;
		setTimeout( onTimeout, 100 );
		function onTimeout() {
			resolve( null, 1 );
		}
	}

	function two( resolve ) {
		/* eslint-disable no-invalid-this */
		this.count += 1;
		setTimeout( onTimeout, 400 );
		function onTimeout() {
			resolve( null, 2 );
		}
	}

	function three( resolve ) {
		/* eslint-disable no-invalid-this */
		this.count += 1;
		setTimeout( onTimeout, 200 );
		function onTimeout() {
			resolve( null, 3 );
		}
	}

	ctx = {
		'count': 0
	};
	opts = {
		'thisArg': ctx
	};
	parallel = factory( [one, two, three], opts, done );

	parallel();

	function done( error ) {
		t.strictEqual( this.count, 3, 'updated provided context' );
		if ( error ) {
			t.fail( error.message );
		} else {
			t.pass( 'did not return an error' );
		}
		t.end();
	}
});

tape( 'if an error is encountered while processing an object property, the returned function suspends execution and immediately returns the error (limit = 1)', function test( t ) {
	var parallel;
	var count;
	var opts;

	count = 0;
	function one( resolve ) {
		setTimeout( onTimeout, 100 );
		function onTimeout() {
			count += 1;
			resolve( new Error( 'beep' ) );
		}
	}

	function two( resolve ) {
		setTimeout( onTimeout, 400 );
		function onTimeout() {
			count += 1;
			resolve( null, 2 );
		}
	}

	function three( resolve ) {
		setTimeout( onTimeout, 200 );
		function onTimeout() {
			count += 1;
			resolve( null, 3 );
		}
	}

	opts = {
		'limit': 1
	};
	parallel = factory( [one, two, three], opts, done );

	parallel();

	function done( error ) {
		t.strictEqual( count, 1, 'suspends execution' );
		if ( error ) {
			t.pass( error.message );
		} else {
			t.fail( 'did not return an error' );
		}
		t.end();
	}
});

